import React from 'react'
import {useParams} from 'react-router-dom' 

const Supplies = () => {

    const {id} = useParams();

    const [datos, setDatos] = React.useState([]);

    React.useEffect(() => {
        const obtenerDatos = async() => {

            const data = await fetch(`http://localhost:1997/api/supplies`);
            const datos = await data.json()
            console.log(datos.body);
            setDatos(datos.body);
        };
        obtenerDatos();
    }, [id]);



    return (
        <div className="container-fluid">

        <div className='row'>
            <div className='col-6 border text-center p-5'>
            <select className="form-select text-center" aria-label="Default select example">
                <option defaultValue>Open this select menu</option>



                {

                    datos.map(item => (


                        <option value="1" key={item._id}>{item.name}</option>



                    ))
                }

            </select>
            </div>

            <div className="col-6">
            <h1 className="text-center mt-3">Datos:</h1>
            <form className="" onSubmit={cargarDatos}>
                <div className="p-5">
                <div className="form-group">
                    <label>Nombre</label>
                    <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="" placeholder="Ingresar nombre" name="nombre"  />

                </div>

                <div className="form-group">
                    <label >Edad</label>
                    <input type="number" className="form-control" id="exampleInputPassword1" placeholder="Ingresar edad" name="edad"  />
                </div>


                <button type="submit" className="btn btn-outline-primary">Cargar</button>
                </div>



            </form>
        </div>
        </div>
        </div>
    )
}

export default Supplies
