    const [datos, setDatos] = React.useState({
        productID: '',
        version: '',
        label: '',
        bin: File

    });


    //CAPTURADOR DE DATOS INGRESADOS EN EL INPUT EN TIEMPO REAL
    const controladorInput = (event) => {

        setDatos({
            //copia los datos para no reescribirlo
            ...datos,
            [event.target.name]: event.target.value

        });
    };


